const Item = ({ itemData })=>{
  console.log("------------")
  console.log(itemData)
  const data = itemData
  return (
    <div className='producto'>
      <h3>{data.producto.nombre}</h3>
      <p>{data.producto.descripcion}</p>
      <h5>En stock: <span>{data.stock}</span></h5>
      <button>COMPRAR</button>
    </div>
  )
}

export default Item;
