const fs = require('fs');

const jsonHelper = {
    leer: function (nombreArchivo) { // pelicula
      return JSON.parse(fs.readFileSync(nombreArchivo))
    },
    escribir: function (nombreArchivo, datos) {
        let pasarAJSON = JSON.stringify(datos)
        return fs.writeFileSync(nombreArchivo + ".json", pasarAJSON)
    },
};

module.exports = jsonHelper

